import plusIcon from '../assets/images/icon-plus.svg';
import minusIcon from '../assets/images/icon-minus.svg';
const Question = ({title, para, handleClick}) => {
  return (
    <div className="question">
        <div className="question-title" onClick={handleClick}>
          <h2>What is Frontend Mentor, and how will it help me?</h2>
        <img src={state?minusIcon:plusIcon} alt="plus icon" />
        </div>
        <div className={state?'answer open':'answer'}>
          <p>Frontend Mentor offers realistic coding challenges to help developers improve their 
  frontend coding skills with projects in HTML, CSS, and JavaScript. It's suitable for 
  all levels and ideal for portfolio building.</p>
        </div>
      </div>
  )
}
export default Question
